import axios from "axios";
import { GET_ERRORS, GET_PROJECTS, GET_PROJECT, DELETE_PROJECT  } from "./type";
export const createProject = (project, history) => async dispatch => {
  try {
    const res = await axios.post("http://localhost:8000/api/project", project);
    history.push("/dashboard");
  } catch (error) {
    dispatch({
      type: GET_ERRORS,
      payload: error.response.data
    });
  }
};


//http://192.168.2.108:8000/api/project


export const getProjects = () => async dispatch => {
  const res = await axios.get("http://localhost:8000/api/project/all");
  dispatch({
    type: GET_PROJECTS,
    payload: res.data
  });
};
export const getProject = (id, history) => async dispatch => {
  const res = await axios.get(`http://localhost:8000/api/project/${id}`);
  dispatch({
    type: GET_PROJECT,
    payload: res.data
  });
};

export const deleteProject = id => async dispatch => {
  await axios.delete(`http://localhost:8000/api/project/${id}`);
  dispatch({
    type: DELETE_PROJECT,
    payload: id
  });
};
